class Colorize:
    def __init__(self, dino, region_1=0, region_2=0, region_3=0, region_4=0, region_5=0, region_6=0):
        self.dino = dino
        self.region_1 = region_1
        self.region_2 = region_2
        self.region_3 = region_3
        self.region_4 = region_4
        self.region_5 = region_5
        self.region_6 = region_6

    def __str__(self):
        return "{} {} {} {} {} {}".format(self.region_1, self.region_2, self.region_3, self.region_4,
                                          self.region_5, self.region_6)


