def a2minusb2(a,b):
    a2minusb2= (a+b)*(a-b)

    return a2minusb2 


def a3minusb3(a,b):
    a3minusb3= (a-b)*(a**2+a*b+b*b)

    return a3minusb3

def a3plusb3(a,b):
    a3plusb3= (a+b)* (a**2-a*b+b*b)
    return a3plusb3

def a4miniusb4(a,b):
    a4miniusb4= (a**2-b**2)*(a**2+b**2)
    return a4miniusb4


def a5miniusb5(a,b):
    a5miniusb5 = (a-b)*(a**4 + a**3*b + a**2*b**2 + a*b**3 + b**4 )
    return a5miniusb5

def a5plusb5(a,b):
    a5miniusb5 = (a+b)*(a**4 - a**3*b + a**2*b**2 + a*b**3 + b**4 )