def union(lists):
    """
    Return lists of sets Union
    >>> from riyazi import* 
    >>> list1 = [3,2,1,8,5,3,1]
    >>> list2 = [9,5,6,3,4,2]
    >>> list3 = [1,0,9,2,8,5,4]
    >>> list4 = [5,3,6,8,2,2,0]
    >>> union([list1,list2,list3,list4])
    
    
    Refrence:
    ::
    # Wikipedia
    # ---------
    
    
    """
    return union(lists)


def semmetric(A,B):
    """
    Return semmetric sets 
    
    >>> from riyazi import* 
    >>> 
    """
    
    return symmetric(A,B) 